package com.cg.test;

public class Calculator {
	public Calculator() {
		// TODO Auto-generated constructor stub
		System.out.println("---Calculator Constructor---");
	}
	public int add(int x, int y) {
		return x+y;
	}
	public int product(int x, int y) {
		return x*y;
	}
	public int div(int x, int y) {
		return x/y;
	}
}
